import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/blogs', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBlogs(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchBlogs();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/blogs/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBlogs(blogs.filter(blog => blog._id !== id));
      } catch (err) {
        console.error(err);
      }
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>My Dashboard</h2>
      <Link to="/add-blog" className="btn">Add New Blog</Link>
      <h3>My Blogs</h3>
      {blogs.length === 0 ? (
        <p>You haven't created any blogs yet.</p>
      ) : (
        blogs.map(blog => (
          <div key={blog._id} className="card">
            <h3>{blog.title}</h3>
            <p>{blog.content.substring(0, 100)}...</p>
            <p>By: {blog.author.username}</p>
            <p>Created: {new Date(blog.createdAt).toLocaleDateString()}</p>
            <Link to={`/blog/${blog._id}`} className="btn">View</Link>
            <button onClick={() => handleDelete(blog._id)} className="btn" style={{ marginLeft: '10px', backgroundColor: '#ff0000' }}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Dashboard;
